// src/components/DraggableShip.js
import React from 'react';
import { useDrag } from 'react-dnd';
import './DraggableShip.css';

export const ItemTypes = {
  SHIP: 'ship',
};


const DraggableShip = ({ ship, onRotate }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.SHIP,
    // ส่งข้อมูล ship ทั้ง object ไปเลย
    item: ship,
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  // จัดการการคลิกขวา (เพื่อหมุน)
  const handleContextMenu = (e) => {
    e.preventDefault(); // ป้องกันไม่ให้เมนูคลิกขวาของเบราว์เซอร์แสดง
    if (onRotate) {
      onRotate(ship.id);
    }
  };

  return (
    <div
      ref={drag}
      className={`draggable-ship ${ship.orientation}`} // เพิ่ม class 'horizontal' หรือ 'vertical'
      style={{ opacity: isDragging ? 0.5 : 1 }}
      onContextMenu={handleContextMenu} // เพิ่ม event handler
    >
      {Array.from({ length: ship.length }).map((_, i) => (
        <div key={i} className="ship-part"></div>
      ))}
    </div>
  );
};

export default DraggableShip;
