// src/components/DraggableShip.js
import React from 'react';
import { useDrag } from 'react-dnd';
import './DraggableShip.css';

// เราจะกำหนด Type ของ Item ที่ลากได้
export const ItemTypes = {
  SHIP: 'ship',
};

const DraggableShip = ({ ship }) => {
  // useDrag คือ hook จาก react-dnd
  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.SHIP,
    item: { id: ship.id, length: ship.length }, // ข้อมูลของเรือที่ถูกลาก
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(), // เช็คว่ากำลังถูกลากอยู่หรือไม่
    }),
  }));

  // สร้าง Style สำหรับเรือ
  const shipStyle = {
    // ทำให้เรือโปร่งใสตอนกำลังลาก
    opacity: isDragging ? 0.5 : 1,
    // กำหนดความยาวของเรือตาม css grid
    gridColumn: `span ${ship.length}`,
  };

  return (
    // ref={drag} คือการบอกว่าให้ DOM element นี้สามารถลากได้
    <div ref={drag} className="draggable-ship" style={shipStyle}>
      {/* สร้าง block สำหรับแต่ละช่องของเรือ */}
      {Array.from({ length: ship.length }).map((_, i) => (
        <div key={i} className="ship-part"></div>
      ))}
    </div>
  );
};

export default DraggableShip;
